import React from "react";

function Question({ question, index, setSelectedOption }) {
  const handleChange = (e) => {
    setSelectedOption(Number(e.target.value));
  };

  return (
    <div className="questionBox" style={{ margin: "auto", width: "70%" }}>
      <p className="question text-xl font-semibold text-gray-900">{question}</p>
      <div className="optionBox flex justify-center items-center gap-4">
        <span
          className="agreeText text-lg font-semibold"
          style={{ color: "#36c490" }}
        >
          Agree
        </span>
        {[1, 2, 3, 4, 5, 6, 7].map((value) => (
          <input
            key={value}
            type="radio"
            style={{
              backgroundColor: "#fff",
              opacity: 1,
              borderRadius: "50%",
              cursor: "pointer",
              transition: "all 0.25s ease-in-out",
              appearance: "none",
              width:
                value === 4
                  ? "35px"
                  : value === 5
                  ? "45px"
                  : value === 6
                  ? "55px"
                  : value === 7
                  ? "70px"
                  : "70px",
              height:
                value === 4
                  ? "35px"
                  : value === 5
                  ? "45px"
                  : value === 6
                  ? "55px"
                  : value === 7
                  ? "70px"
                  : "70px",
              border: "2px solid",
              borderColor:
                value === 4
                  ? "#00b1af"
                  : value === 5
                  ? "#5188c9"
                  : value === 6
                  ? "#5188c9"
                  : "#5188c9",
            }}
            value={value}
            name={`question${index + 1}`}
            onChange={handleChange}
          />
        ))}
        <span
          className="disagreeText text-lg font-semibold"
          style={{ color: "#5188c9" }}
        >
          Disagree
        </span>
      </div>
    </div>
  );
}

export default Question;
