import React from "react";

function Question({ question, index, setSelectedOption }) {
  const handleChange = (e) => {
    setSelectedOption(Number(e.target.value));
  };

  return (
    <div style={{ margin: "auto", width: "70%" }}>
      <p className="text-xl text-center font-semibold text-gray-900">
        {question}
      </p>
      <div className="flex justify-center items-center gap-4">
        <span className="text-lg text-green-500 font-semibold">Agree</span>
        {[1, 2, 3, 4, 5, 6, 7].map((value) => (
          <input
            key={value}
            type="radio"
            style={{
              backgroundColor: "#fff",
              opacity: 1,
              borderRadius: "50%",
              cursor: "pointer",
              transition: "all 0.25s ease-in-out",
              appearance: "none",
              width:
                value === 4
                  ? "35px"
                  : value === 5
                  ? "45px"
                  : value === 6
                  ? "55px"
                  : value === 7
                  ? "70px"
                  : "70px",
              height:
                value === 4
                  ? "35px"
                  : value === 5
                  ? "45px"
                  : value === 6
                  ? "55px"
                  : value === 7
                  ? "70px"
                  : "70px",
              border: "2px solid",
              borderColor:
                value === 4
                  ? "var(--neutral)"
                  : value === 5
                  ? "var(--disagree)"
                  : value === 6
                  ? "var(--disagree)"
                  : "var(--disagree)",
            }}
            value={value}
            name={`question${index + 1}`}
            onChange={handleChange}
          />
        ))}
        <span className="text-lg text-red-500 font-semibold">Disagree</span>
      </div>
    </div>
  );
}

export default Question;
